#include<iostream>using namespace std;int main(){int countsnake=0;int countmongoose=0;int temp=0;int eat=0;int eated=0;int testcases;char win[100];int l=0;cin>>testcases;for(int i=0;i<testcases;i++){	char c[200];  cin>>c;   for(int j=0;c[j]!='\0';j++)  {if(c[j]=='s')   	{   		if(temp==1&&eat==0)   		{   		temp=2;   		eated=1;   	    }   	    else   	    {   	    countsnake++;   	    temp=2;   	    eated=0;   	    }   	}   	else   	{   		if(temp==2&&eated==0)   		{   			countsnake--;   			countmongoose++;   			temp=1;   			eat=1;   		}   		else   		{   			countmongoose++;   			temp=1;   			eat=0;   		}   	}   }   if(countsnake>countmongoose)   {     win[l]='s';   }   else if(countsnake<countmongoose)   {      win[l]='m';   }   else   {     win[l]='d';   }   l++;   countmongoose=0;   countsnake=0;   eat=0;   temp=0;   eated=0;}for(int a=0;a<testcases;a++){	if(win[a]=='s')   {     cout<<"snakes"<<endl;   }   else if(win[a]=='m')   {     cout<<"mongooses"<<endl;   }   else   {   	cout<<"tie"<<endl;   }	}return 0;}